const { defaults } = require('@istanbuljs/schema');
const Instrumenter = require('istanbul-lib-instrument/src/instrumenter');
const programVisitor = require('istanbul-lib-instrument/src/visitor');
const readInitialCoverage = require('istanbul-lib-instrument/src/read-coverage');

/**
 * createInstrumenter creates a new instrumenter with the
 * supplied options.
 * @param {Object} opts - instrumenter options. See the documentation
 * for the Instrumenter class.
 */
function createInstrumenter(opts) {
    return new Instrumenter(opts);
}

module.exports = {
    createInstrumenter,
    programVisitor,
    readInitialCoverage,
    defaultOpts: defaults.instrumenter
};
